import React from "react";

interface GameInterfaceProps {
  account: string;
  tokenBalance: string;
  onPlayGame: () => void;
  isPlaying: boolean;
}

const GameInterface: React.FC<GameInterfaceProps> = ({
  account,
  tokenBalance,
  onPlayGame,
  isPlaying,
}) => {
  return (
    <div style={{ maxWidth: "600px", margin: "0 auto", padding: "2rem", border: "1px solid #ccc", borderRadius: "8px" }}>
      <h2>Game Interface</h2>
      <p><strong>Account:</strong> {account || "Not connected"}</p>
      <p><strong>Token Balance:</strong> {tokenBalance} GAME</p>
      <button
        onClick={onPlayGame}
        disabled={isPlaying || !account}
        style={{
          padding: "10px 20px",
          fontSize: "16px",
          backgroundColor: isPlaying ? "#ccc" : "#007bff",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: isPlaying || !account ? "not-allowed" : "pointer",
        }}
      >
        {isPlaying ? "Playing..." : "Play Game (Bet 10 GAME)"}
      </button>
      {!account && <p style={{ color: "red" }}>Please connect MetaMask first.</p>}
    </div>
  );
};

export default GameInterface;
