// src/App.tsx

import React, { useEffect, useState } from "react";
import { ethers } from "ethers";
import GameInterface from "./components/GameInterface";
import { NETWORK_PARAMS, TOKEN_ABI, ARENA_ABI } from "./celoConfig";

const App: React.FC = () => {
  const [account, setAccount] = useState<string>("");
  const [tokenBalance, setTokenBalance] = useState<string>("0");
  const [provider, setProvider] = useState<ethers.providers.Web3Provider | null>(null);
  const [tokenContract, setTokenContract] = useState<ethers.Contract | null>(null);
  const [arenaContract, setArenaContract] = useState<ethers.Contract | null>(null);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);

  // Khởi tạo kết nối MetaMask và hợp đồng
  useEffect(() => {
    const init = async () => {
      if ((window as any).ethereum) {
        const web3Provider = new ethers.providers.Web3Provider((window as any).ethereum);
        setProvider(web3Provider);

        // Yêu cầu truy cập tài khoản
        await web3Provider.send("eth_requestAccounts", []);
        const signer = web3Provider.getSigner();
        const acct = await signer.getAddress();
        setAccount(acct);

        // Khởi tạo contract token và arena
        const token = new ethers.Contract(NETWORK_PARAMS.tokenAddress, TOKEN_ABI, signer);
        setTokenContract(token);
        const arena = new ethers.Contract(NETWORK_PARAMS.arenaAddress, ARENA_ABI, signer);
        setArenaContract(arena);

        // Lấy số dư token ban đầu
        const bal = await token.balanceOf(acct);
        setTokenBalance(ethers.utils.formatUnits(bal, 18));
      } else {
        alert("Vui lòng cài MetaMask hoặc tương đương!");
      }
    };
    init().catch(err => {
      console.error("Init error", err);
    });
  }, []);

  // Hàm cập nhật số dư token từ hợp đồng
  const updateBalance = async () => {
    if (tokenContract && account) {
      const bal = await tokenContract.balanceOf(account);
      setTokenBalance(ethers.utils.formatUnits(bal, 18));
    }
  };

  // Hàm khi người dùng nhấn “Play Game”
  const playGame = async () => {
    if (!arenaContract) {
      alert("Contract Arena chưa được khởi tạo!");
      return;
    }
    try {
      setIsPlaying(true);
      // Cược ví dụ 10 token
      const betAmount = ethers.utils.parseUnits("10", 18);
      const tx = await arenaContract.playGame(betAmount);
      await tx.wait();
      // sau khi giao dịch thành công, cập nhật lại số dư
      await updateBalance();
      alert("Chúc mừng! Bạn đã chơi xong.");
    } catch (err: any) {
      console.error("playGame error", err);
      alert("Có lỗi khi chơi game: " + (err?.message || err));
    } finally {
      setIsPlaying(false);
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "2rem" }}>
      <h1>Play-to-Earn Game trên Celo Sepolia</h1>
      <GameInterface
        account={account}
        tokenBalance={tokenBalance}
        onPlayGame={playGame}
        isPlaying={isPlaying}
      />
    </div>
  );
};

export default App;
